INSERT INTO pokedex (no_pokedex, nombre, tipo, descripcion) VALUES
(1, 'Bulbasaur', 'Planta/Veneno', 'Bulbasaur tiene una planta en su espalda que crece con el tiempo. Es conocido por su capacidad para usar movimientos de tipo Planta y Veneno.'),
(2, 'Ivysaur', 'Planta/Veneno', 'Ivysaur es la evolución de Bulbasaur y su planta crece más. Se vuelve más fuerte en batalla y usa ataques más poderosos.'),
(3, 'Venusaur', 'Planta/Veneno', 'Venusaur es la forma evolucionada final de Bulbasaur. La planta en su espalda florece completamente, y es muy poderoso en batallas.'),
(4, 'Charmander', 'Fuego', 'Charmander es un Pokémon de tipo Fuego con una llama en la cola que indica su salud. Es conocido por su temperamento ardiente.'),
(5, 'Charmeleon', 'Fuego', 'Charmeleon es la evolución de Charmander. Su llama se vuelve más intensa cuando está enojado. Es conocido por su actitud impetuosa.'),
(6, 'Charizard', 'Fuego/Volador', 'Charizard, la evolución final de Charmander, puede volar y su llama puede alcanzar temperaturas extremas. Es uno de los Pokémon más poderosos de su tipo.'),
(7, 'Squirtle', 'Agua', 'Squirtle es un Pokémon de tipo Agua que utiliza su caparazón para protegerse y nadar con agilidad. Es conocido por su lealtad.'),
(8, 'Wartortle', 'Agua', 'Wartortle es la evolución de Squirtle. Su caparazón se hace más grande y sus habilidades acuáticas mejoran considerablemente.'),
(9, 'Blastoise', 'Agua', 'Blastoise, la evolución final de Squirtle, tiene cañones de agua en su caparazón que puede usar en combate. Es extremadamente fuerte en batallas.'),
(10, 'Caterpie', 'Bicho', 'Caterpie es un Pokémon de tipo Bicho que evoluciona rápidamente. Es conocido por sus movimientos de tipo Bicho y su aspecto de oruga.'),
(11, 'Metapod', 'Bicho', 'Metapod es la etapa de pupa de Caterpie. Está en un capullo y está en proceso de transformación a Butterfree.'),
(12, 'Butterfree', 'Bicho/Volador', 'Butterfree es la evolución final de Caterpie. Tiene alas grandes y es conocido por su capacidad para usar movimientos de tipo Bicho y Volador.'),
(13, 'Weedle', 'Bicho/Veneno', 'Weedle es un Pokémon de tipo Bicho y Veneno. Tiene un aguijón venenoso en su cabeza que usa para defenderse.'),
(14, 'Kakuna', 'Bicho/Veneno', 'Kakuna es la etapa de pupa de Weedle. Está en un estado de inmovilidad mientras se transforma en Beedrill.'),
(15, 'Beedrill', 'Bicho/Veneno', 'Beedrill es la evolución final de Weedle. Tiene dos aguijones y es conocido por su agresividad y sus movimientos venenosos.'),
(16, 'Pidgey', 'Normal/Volador', 'Pidgey es un Pokémon de tipo Normal y Volador. Es conocido por su capacidad para volar y su comportamiento en bandadas.'),
(17, 'Pidgeotto', 'Normal/Volador', 'Pidgeotto es la evolución de Pidgey. Es más fuerte y puede volar a mayores altitudes.'),
(18, 'Pidgeot', 'Normal/Volador', 'Pidgeot es la evolución final de Pidgey. Tiene alas grandes y es muy rápido en vuelo. Es conocido por su gran habilidad en el vuelo.'),
(19, 'Rattata', 'Normal', 'Rattata es un Pokémon de tipo Normal conocido por su alta velocidad y su capacidad para morder con fuerza.'),
(20, 'Raticate', 'Normal', 'Raticate es la evolución de Rattata. Tiene colmillos afilados y es muy agresivo en combate.'),
(21, 'Spearow', 'Normal/Volador', 'Spearow es un Pokémon de tipo Normal y Volador. Es conocido por su naturaleza agresiva y su capacidad para atacar desde el aire.'),
(22, 'Fearow', 'Normal/Volador', 'Fearow es la evolución de Spearow. Tiene un gran tamaño y es muy rápido, con un pico afilado que utiliza en combate.'),
(23, 'Ekans', 'Veneno', 'Ekans es un Pokémon de tipo Veneno con forma de serpiente. Su veneno es peligroso y puede inmovilizar a sus presas.'),
(24, 'Arbok', 'Veneno', 'Arbok es la evolución de Ekans. Tiene un patrón en su cuerpo que usa para intimidar a sus enemigos y su veneno es extremadamente potente.'),
(25, 'Pikachu', 'Eléctrico', 'Pikachu es un Pokémon de tipo Eléctrico y es el ícono de la franquicia Pokémon. Es conocido por su habilidad para generar electricidad.'),
(26, 'Raichu', 'Eléctrico', 'Raichu es la evolución de Pikachu. Es más grande y su habilidad para generar electricidad es mucho más potente.'),
(27, 'Sandshrew', 'Tierra', 'Sandshrew es un Pokémon de tipo Tierra con forma de armadillo. Es conocido por su capacidad para excavar y protegerse en el suelo.'),
(28, 'Sandslash', 'Tierra', 'Sandslash es la evolución de Sandshrew. Tiene espinas en su espalda que usa para defenderse y es muy resistente.'),
(29, 'Nidoran♀', 'Veneno', 'Nidoran♀ es un Pokémon de tipo Veneno con un pequeño cuerno en su frente. Es conocido por su naturaleza defensiva.'),
(30, 'Nidorina', 'Veneno', 'Nidorina es la evolución de Nidoran♀. Tiene un tamaño mayor y es conocida por su agresividad en combate.'),
(31, 'Nidoqueen', 'Veneno/Tierra', 'Nidoqueen es la evolución final de Nidoran♀. Es muy poderosa y tiene una gran resistencia en batalla.'),
(32, 'Nidoran♂', 'Veneno', 'Nidoran♂ es un Pokémon de tipo Veneno con un cuerno en su frente. Es conocido por su naturaleza territorial.'),
(33, 'Nidorino', 'Veneno', 'Nidorino es la evolución de Nidoran♂. Tiene un cuerno más grande y es muy agresivo en combate.'),
(34, 'Nidoking', 'Veneno/Tierra', 'Nidoking es la evolución final de Nidoran♂. Es extremadamente fuerte y tiene un cuerno poderoso que usa en batalla.'),
(35, 'Clefairy', 'Hada', 'Clefairy es un Pokémon de tipo Hada conocido por su capacidad para usar movimientos de tipo Hada y su actitud alegre.'),
(36, 'Clefable', 'Hada', 'Clefable es la evolución de Clefairy. Es conocido por su habilidad para cambiar su forma y su habilidad para usar movimientos de tipo Hada con eficacia.'),
(37, 'Vulpix', 'Fuego', 'Vulpix es un Pokémon de tipo Fuego con seis colas. Es conocido por su belleza y su habilidad para lanzar llamas.'),
(38, 'Ninetales', 'Fuego', 'Ninetales es la evolución de Vulpix. Tiene nueve colas y es conocido por su habilidad para lanzar llamas y su longevidad.'),
(39, 'Jigglypuff', 'Normal/Hada', 'Jigglypuff es un Pokémon de tipo Normal y Hada. Es conocido por su habilidad para cantar una canción que induce el sueño.'),
(40, 'Wigglytuff', 'Normal/Hada', 'Wigglytuff es la evolución de Jigglypuff. Su habilidad para cantar y su gran capacidad pulmonar lo hacen un adversario difícil en combate.'),
(41, 'Zubat', 'Veneno/Volador', 'Zubat es un Pokémon de tipo Veneno y Volador conocido por su habilidad para volar en la oscuridad y su ataque de mordida venenosa.'),
(42, 'Golbat', 'Veneno/Volador', 'Golbat es la evolución de Zubat. Tiene un tamaño mayor y es conocido por su habilidad para absorber salud de sus oponentes.'),
(43, 'Oddish', 'Planta/Veneno', 'Oddish es un Pokémon de tipo Planta y Veneno con una planta en su cabeza. Es conocido por su habilidad para liberar esporas venenosas.'),
(44, 'Gloom', 'Planta/Veneno', 'Gloom es la evolución de Oddish. Tiene un aroma desagradable que utiliza para repeler a los enemigos y su habilidad para usar movimientos de tipo Planta y Veneno.'),
(45, 'Vileplume', 'Planta/Veneno', 'Vileplume es la evolución final de Oddish. Tiene una enorme flor en su cabeza que libera esporas venenosas y es muy poderoso en combate.'),
(46, 'Paras', 'Bicho/Planta', 'Paras es un Pokémon de tipo Bicho y Planta con hongos en su espalda que le ayudan a obtener nutrientes del suelo.'),
(47, 'Parasect', 'Bicho/Planta', 'Parasect es la evolución de Paras. Los hongos en su espalda crecen mucho más y se vuelven muy poderosos en combate.'),
(48, 'Venonat', 'Bicho/Veneno', 'Venonat es un Pokémon de tipo Bicho y Veneno con ojos grandes que le permiten detectar a sus enemigos con facilidad.'),
(49, 'Venomoth', 'Bicho/Veneno', 'Venomoth es la evolución de Venonat. Tiene alas grandes y es conocido por su habilidad para liberar un polvo venenoso.'),
(50, 'Diglett', 'Tierra', 'Diglett es un Pokémon de tipo Tierra que vive bajo tierra. Es conocido por su velocidad para excavar y moverse rápidamente.'),
(51, 'Dugtrio', 'Tierra', 'Dugtrio es la evolución de Diglett. Está compuesto por tres Diglett unidos que se mueven bajo tierra con gran velocidad.'),
(52, 'Meowth', 'Normal', 'Meowth es un Pokémon de tipo Normal que se parece a un gato. Es conocido por su habilidad para usar el movimiento "Pay Day" para recolectar dinero.'),
(53, 'Persian', 'Normal', 'Persian es la evolución de Meowth. Es un Pokémon elegante y rápido, conocido por su agilidad en combate.'),
(54, 'Psyduck', 'Agua', 'Psyduck es un Pokémon de tipo Agua conocido por sus dolores de cabeza, que pueden desencadenar poderes psíquicos en momentos de estrés.'),
(55, 'Golduck', 'Agua', 'Golduck es la evolución de Psyduck. Es un Pokémon rápido y poderoso en el agua, conocido por su habilidad para usar ataques acuáticos.'),
(56, 'Mankey', 'Lucha', 'Mankey es un Pokémon de tipo Lucha con un temperamento explosivo. Es conocido por su habilidad para usar movimientos de tipo Lucha con gran fuerza.'),
(57, 'Primeape', 'Lucha', 'Primeape es la evolución de Mankey. Su ira es legendaria y su fuerza en combate es muy grande.'),
(58, 'Growlithe', 'Fuego', 'Growlithe es un Pokémon de tipo Fuego que se asemeja a un cachorro de león. Es conocido por su lealtad y su habilidad para usar ataques de fuego.'),
(59, 'Arcanine', 'Fuego', 'Arcanine es la evolución de Growlithe. Es un Pokémon majestuoso y rápido con gran fuerza en batalla y habilidades de fuego.'),
(60, 'Poliwag', 'Agua', 'Poliwag es un Pokémon de tipo Agua con una apariencia de renacuajo. Es conocido por su habilidad para nadar y usar ataques acuáticos.'),
